var timezoneSelectorLink = $E('timezoneSelectorLink');

var tzSelector = new TimezoneSelector(
        timezoneSelectorLink,
        'LOCAL',
        'Europe/Paris',
        null,
        null,
        'http://indico.cern.ch/change-timezone'
);

$("#timezoneSelectorLink").qtip({

    style: {
        width: '300px',
        classes: 'qtip-rounded qtip-shadow qtip-popup qtip-timezone',
        tip: {
            corner: true,
            width: 20,
            height: 15
        }
    },
    position: {
        my: 'top center',
        at: 'bottom center'
    },
    content: function(api){
        return $(tzSelector.getContent().dom);
        },
    show: {
        event: "click",
        effect: function() {
            $(this).fadeIn(300);
        }
    },
    hide: {
        event: 'unfocus click',
        fixed: true,
        effect: function() {
            $(this).fadeOut(300);
        }
    }
});

var languageLink = $E('languageSelectorLink');
var languageMenu = null;
languageLink.observeClick(function(e) {
    // Close the menu if clicking the link when menu is open
    if (languageMenu != null && languageMenu.isOpen()) {
        languageMenu.close();
        languageMenu = null;
        return;
    }

    var menuItems = {};
    var form = $E('languageForm');
    var inputHidden = $E('languageInputHidden');

    // build a dictionary that represents the menu
        menuItems['English'] = {action:function() {inputHidden.dom.value = 'en_GB'; form.dom.submit()}, display:'English'};
        menuItems['español'] = {action:function() {inputHidden.dom.value = 'es_ES'; form.dom.submit()}, display:'español'};
        menuItems['français'] = {action:function() {inputHidden.dom.value = 'fr_FR'; form.dom.submit()}, display:'français'};

    languageMenu = new PopupMenu(menuItems, [languageLink], 'darkPopupList', true, true);
    var pos = languageLink.getAbsolutePosition();
    languageMenu.open(pos.x + languageLink.dom.offsetWidth + 10, pos.y + languageLink.dom.offsetHeight + 3, null, null, false, true);

    return false;
});

var setURLs = function(urls){
    if($('#detailExporta73513').prop("checked")){
        $('#publicLinka73513').val(urls["publicRequestDetailedURL"]).attr('title', urls["publicRequestDetailedURL"]);
        $('#authLinka73513').val(urls["authRequestDetailedURL"]).attr('title', urls["authRequestDetailedURL"]);
    }else{
        $('#publicLinka73513').val(urls["publicRequestURL"]).attr('title', urls["publicRequestURL"]);
        $('#authLinka73513').val(urls["authRequestURL"]).attr('title', urls["authRequestURL"]);
    }
};

exportPopups["a73513"] = new ExportIcalInterface(2, false, true, false, false, setURLs, 'event.api.getExportURLs', {confId:"73513"}, {"authRequestURL": "", "authRequestDetailedURL": "", "publicRequestURL": "https:\/\/indico.cern.ch\/export\/event\/73513.ics", "publicRequestDetailedURL": "https:\/\/indico.cern.ch\/export\/event\/73513.ics?detail=contributions"}, "a73513", "");

$("body").delegate('#detailExporta73513', "click", function(e) {
    setURLs(exportPopups["a73513"].getRequestURLs());
});

